INSERT INTO `news` VALUES (1,'kk','АДАМ ПАПИЛЛОМА ВИРУСЫ ЖӘНЕ ЖАТЫР МОЙНЫ ОБЫРЫ','ВПЧ И РАК ШЕЙКИ МАТКИ','<p style=\"text-align: justify; text-align: justify; text-align: justify; text-align: justify; ;\"><span style=\"white-space: pre-wrap;\"><strong><em>Адам папиллома вирусынан (АПВ)</em></strong> туындаған жатыр мойны обыры (ЖМО) әлемдегі ең көп таралған қатерлі ісіктердің бірі болып табылады, бұл барлық жағдайлардың 13,1% құрайды. Жыл сайын бұл аурудан 300 мыңға жуық әйел қайтыс болады. Қазақстанда ЖМО барлық жастағы әйелдер арасында таралуы бойынша қатерлі ісіктің басқа түрлері арасында екінші орында тұр. Жыл сайын шамамен 1900 жаңа жағдай тіркеледі, 600-ден астам әйел осы патологиядан қайтыс болады.</span></p><p style=\"text-align: justify; text-align: justify; text-align: justify; text-align: justify; ;\"><span style=\"white-space: pre-wrap;\">АПВ туындаған ЖМО дамуын болдырмаудың жалғыз әдісі-вакцинация. Қазақстанның ұлттық вакцинация күнтізбесіне және Дүниежүзілік денсаулық сақтау ұйымының ұсынымдарына сәйкес, 11 жастағы қыздар 6 ай аралықпен АПВ қарсы екі рет вакцинация алуы керек. Елде АПВ ең онкогенді түрлерінен қорғауды қамтамасыз ететін төрт валентті вакцина қолданылады(6, 11, 16, 18).</span></p>','<p style=\"text-align: justify; text-align: justify; text-align: justify; text-align: justify; ;\"><strong><em>Рак шейки матки (РШМ)</em></strong>, вызываемый вирусом папилломы человека (ВПЧ), представляет собой один из наиболее распространенных видов рака в мире, составляя до 13,1% всех случаев. Каждый год от этой болезни умирает около 300 тыс. женщин. В Казахстане РШМ занимает второе место среди других видов рака по распространенности среди женщин всех возрастов. Ежегодно регистрируется около 1900 новых случаев, причем более 600 женщин умирает от этой патологии.</p><p style=\"text-align: justify; text-align: justify; text-align: justify; text-align: justify; ;\">Единственным методом предотвращения развития РШМ, вызванного ВПЧ, является вакцинация. В соответствии с Национальным календарем вакцинации Казахстана и рекомендациями Всемирной организации здравоохранения, девочки в возрасте 11 лет должны получать двукратную вакцинацию против ВПЧ с интервалом в 6 месяцев. В стране используется четырехвалентная вакцина, обеспечивающая защиту от наиболее онкогенных типов ВПЧ (6, 11, 16, 18).</p>','adam-papilloma-virusy-zane-zatyr-moiny-obyry','01J35E14JFJAA8M0CGBB5WKFA4.jpg','sdsd','sdsd',1,'2024-05-10 00:00:00','2024-05-10 19:55:33','2024-07-19 07:03:53'),(2,'kk','ҚАЗАҚСТАНДА ҚЫЗЫЛШАНЫҢ КЕҢ ТАРАЛУЫ','МАСШТАБНАЯ ВСПЫШКА КОРИ В КАЗАХСТАНЕ','<p style=\"text-align: justify; text-align: justify; text-align: justify; ;\">2023 жылдың соңында Қазақстанда қызылшаның соңғы 30 жылдағы ең ауқымдыкаеті басталды, ол 2019 жылы болған алдыңғы эпидеттің көрсеткішінен 2,1 есе асып түсті.</p><p style=\"text-align: justify; text-align: justify; text-align: justify; ;\">Қызылшаның белсенді таралуының алғашқы жағдайлары шамамен бір жыл бұрын, 2023 жылдың наурызында тіркелді. Сол кезде еліміздің бас санитарлық дәрігері Айжан Есмағамбетова жеті облыста анықталған инфекцияның 20 жағдайы туралы хабарлады. Көктемде маусымдық ауытқуларға және таралудың төмендеуіне ұшыраған ауру елде және жазда белсенді түрде тарала берді. Шілдеге қарай тіркелген жағдайлардың саны екі мыңға дейін өсті және өсу үрдісі жалғасты.</p><p style=\"text-align: justify; text-align: justify; text-align: justify; ;\">2024 жылдың қаңтар айының басынан бері қызылшаның 825 зертханалық диагнозы расталды. Олардың көпшілігі 14 жасқа дейінгі балалар (495 жағдай), жасөспірімдер (107 жағдай) және ересектер (223 жағдай).</p>','<p style=\"text-align: justify; text-align: justify; text-align: justify; ;\">В конце 2023 года началась самая масштабная за последние 30 лет вспышка кори в Казахстане, превысившая показатели предыдущей эпидемии в 2,1 раза, которая произошла в 2019 году.</p><p style=\"text-align: justify; text-align: justify; text-align: justify; ;\">Первые случаи активного распространения кори были зафиксированы почти год назад, в марте 2023 года. На тот момент главный санитарный врач страны, Айжан Есмагамбетова, сообщала о 20 случаях заражения, выявленных в семи областях. Заболевание, подверженное сезонным колебаниям и уменьшению распространения весной, продолжало активно распространяться в стране и летом. К июлю число зарегистрированных случаев возросло до двух тысяч, и тренд на увеличение продолжался.</p><p style=\"text-align: justify; text-align: justify; text-align: justify; ;\">С начала января 2024 года было подтверждено 825 лабораторно диагностированных случаев кори. Среди них большинство составляют дети до 14 лет (495 случаев), подростки (107 случаев) и взрослое население (223 случая).</p>','qazaqstanda-qyzylsanyn-ken-taraluy','01J35E38KDP6D6KND22V0V6ACD.jpg','ddddddddd','ddddddddddd',1,'2024-05-10 00:00:00','2024-05-10 20:10:34','2024-07-19 07:05:03');
/*!40000 ALTER TABLE `news` ENABLE KEYS */;
UNLOCK TABLES;

