INSERT INTO `test_results` VALUES (2,0,49,'Сіздің балаларға вакцинация туралы хабардар болу деңгейіңіз біршама төмен.\nЕсіңізде болсын, вакцинация жұқпалы аурулардың алдын алудың ең тиімді әдістерінің бірі болып табылады. Сондықтан біздің сайтта ұсынылған ақпаратпен танысуға және осы сауалнаманы қайта өтуге кеңес береміз.','Ваш уровень информированности о детской вакцинации является несколько недостаточным.\nПомните, что вакцинация является одним из наиболее эффективных методов предотвращения инфекционных заболеваний. Поэтому рекомендуем вам ознакомиться с информацией, которая предоставлена на нашем сайте и повторно пройти данный опросник.','-'),(3,50,74,'Сіз вакцинация туралы біршама хабардарсыз. \nВакцинация және расталған медициналық деректер туралы қосымша ақпаратты біздің сайттан немесе ДДҰ ресми беттерінен алуға болады.','У вас есть некоторые знания о прививках.\nДополнительную информацию о прививках и подтвержденных медицинских данных можно получить на нашем сайте или на официальных страницах ВОЗ','-'),(4,75,100,'Сіз иммундау жөнінде өте жақсы хабардарсыз!','Вы отлично информированы!','-');
/*!40000 ALTER TABLE `test_results` ENABLE KEYS */;
UNLOCK TABLES;

